            array(
                'form_id'   => 'tfseekfid',
                'page'      => 'tfseekpage',
                'orderby'   => 'tfseekorderby'
            ),
            $params
        );

        $this->search_parameters = apply_filters('tf_ext_seek_search_parameters', $params);
    }

